mapper.select:
select

from resource
where `name` = #{name,jdbcType=VARCHAR} and project_id = #{projectId,jdbcType=INTEGER}

select
from resource where is_delete = 0 and (permission = 1 or owner_id = #{ownerId,jdbcType=INTEGER})
and type = #{type,jdbcType=TINYINT}
and `name` like concat('%', #{name,jdbcType=VARCHAR}, '%')
order by gmt_create desc limit ${startIndex}, 10

select count(1) from resource where is_delete = 0 and (owner_id = #{ownerId,jdbcType=INTEGER} or permission = 1)
and type = #{type,jdbcType=TINYINT}
and `name` like concat('%', #{name,jdbcType=VARCHAR}, '%')

mapper.update:
update resource
set is_delete = 1, gmt_modified = now()
where id = #{resourceId,jdbcType=INTEGER}
