mapper.select:
select
from tool where is_delete = 0 and is_test_step = #{isTestStep,jdbcType=TINYINT} and (owner_id = #{ownerId,jdbcType=VARCHAR} or permission = 1)
and type = #{type,jdbcType=TINYINT}
and title like concat('%', #{title,jdbcType=VARCHAR}, '%')
order by id desc limit ${startIndex}, 10
