mapper.update:
update user
set is_delete = 1, gmt_modified = now()
where user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
select

from user
where is_delete = 0
and pass_word = #{password,jdbcType=VARCHAR}

  and login_name = #{loginName,jdbcType=VARCHAR}


  and phone = #{phone,jdbcType=VARCHAR}
