mapper.select:
select a.user_id, a.project_id, a.type, b.username, b.nickname from user_project_relation a inner join user b on a.user_id = b.id where a.project_id = #{projectId,jdbcType=INTEGER} and b.is_delete = 0
and `nickname` = #{nickname,jdbcType=VARCHAR}
order by a.gmt_create desc limit ${startIndex}, 10

mapper.insert:
insert into user_project_relation (user_id, project_id, `type`, 
  is_delete, gmt_create, gmt_modified
  )
values (#{userId,jdbcType=INTEGER}, #{projectId,jdbcType=INTEGER}, #{type,jdbcType=TINYINT}, 
  0, now(), now()
  )
